            <v-col cols="9">
            <v-row justify="start" justify-md="end" class="pl-3">
                <icon
                v-if="signedin"
                class="personicon mr-2 mt-1 univie-grey"
                name="material-social-person"
                width="24px"
                height="24px"
                ></icon>
                <span
                v-if="signedin"
                class="mr-2 mt-2 subheading displayname univie-grey"
                >{{ user.firstname }} {{ user.lastname }}</span
                >

                <v-menu offset-y>
                <template v-slot:activator="{ on }">
                    <v-btn text v-on="on" class="top-margin-lang">
                    <span class="grey--text text--darken-1">{{
                        localeLabel
                    }}</span>
                    <icon
                        name="univie-sprache"
                        class="lang-icon grey--text text--darken-1"
                    ></icon>
                    </v-btn>
                </template>
                <v-list>
                    <v-list-item
                    v-if="useLocale('eng')"
                    @click="changeLocale('eng')"
                    >
                    <v-list-item-title>English</v-list-item-title>
                    </v-list-item>
                    <v-list-item
                    v-if="useLocale('deu')"
                    @click="changeLocale('deu')"
                    >
                    <v-list-item-title>Deutsch</v-list-item-title>
                    </v-list-item>
                </v-list>
                </v-menu>
            </v-row>

            <v-row>
                <v-col
                v-if="appconfig.showinstanceswitch"
                cols="4"
                class="select-instance text-left"
                >
                <v-select
                    :items="instances"
                    @input="switchInstance"
                    :value="instanceconfig.baseurl"
                    item-text="baseurl"
                    single-line
                ></v-select>
                </v-col>
                <v-col
                class="text-left"
                cols="10"
                offset="1"
                v-else-if="instanceconfig.title"
                >
                <icon
                    left
                    dark
                    name="univie-right"
                    color="#a4a4a4"
                    width="14px"
                    height="14px"
                    class="mb-1"
                ></icon>
                <nuxt-link
                    class="subheading primary--text mx-3"
                    :to="localePath('/')"
                    >{{ instanceconfig.title }}</nuxt-link
                >
                </v-col>
            </v-row>

            <v-row>
                <v-toolbar flat color="white" dense>
                <client-only>
                    <v-app-bar-nav-icon class="hidden-md-and-up">
                    <v-menu offset-y>
                        <template v-slot:activator="{ on }">
                        <v-btn text icon color="grey lighten-1" v-on="on"
                            ><icon
                            name="material-navigation-menu"
                            width="24px"
                            height="24px"
                            ></icon
                        ></v-btn>
                        </template>
                        <v-list>
                        <v-list-item
                            @click="
                            $router.push(
                                localeLocation({
                                path: '/search',
                                query: { reset: 1 },
                                })
                            )
                            "
                            ><v-list-item-title>{{
                            $t("Search")
                            }}</v-list-item-title></v-list-item
                        >
                        <v-list-item
                            v-if="signedin"
                            @click="
                            $router.push(localeLocation({ path: '/submit' }))
                            "
                            ><v-list-item-title>{{
                            $t("Submit")
                            }}</v-list-item-title></v-list-item
                        >
                        <v-list-item
                            v-if="signedin"
                            @click="
                            $router.push(
                                localeLocation({
                                path: '/search',
                                query: { reset: 1, owner: user.username },
                                })
                            )
                            "
                            ><v-list-item-title>{{
                            $t("My objects")
                            }}</v-list-item-title></v-list-item
                        >
                        <v-list-item
                            v-if="signedin"
                            @click="
                            $router.push(localeLocation({ path: '/lists' }))
